# abs() function
print("abs(-50) : ",abs(-50))
print("abs(100.53) : ",abs(100.53))

# ceil() function
import math
print("math.ceil(-45.32) : ",math.ceil(-45.32))
print("math.ceil(10.52) : ",math.ceil(10.52))
print("math.ceil(85.34) : ",math.ceil(85.34))
print("math.ceil(math.pi) : ",math.ceil(math.pi))


# exp() function
print("math.exp(-45.32) : ",math.exp(-45.32))
print("math.exp(10.52) : ",math.exp(10.52))
print("math.exp(85.34) : ",math.exp(85.34))
print("math.exp(math.pi) : ",math.exp(math.pi))


# log() function  
print("math.log(10.52) : ",math.log(10.52))
print("math.log(85.34) : ",math.log(85.34))
print("math.log(math.pi) : ",math.log(math.pi))

#log10() function
print("math.log10(10.52) : ",math.log10(10.52))
print("math.log10(85.34) : ",math.log10(85.34))
print("math.log10(math.pi) : ",math.log10(math.pi))

# floor function
print("math.floor(-52.42) : ",math.floor(-52.42) )
print("math.floor(10.52) : ",math.floor(10.52))
print("math.floor(85.34) : ",math.floor(85.34))
print("math.floor(math.pi) : ",math.floor(math.pi))


#max() function
print("max(100,530,-50,37) : ",max(100,530,-50,37))
print("max(10,50,-50,300) : ",max(10,50,-50,300))
print("max(900,530,-50,-837) : ",max(900,530,-50,837))
print("max(105,400,-500,370) : ",max(105,400,-500,370))


#min() function
print("min(100,530,-50,37) : ",min(100,530,-50,37))
print("min(10,50,-50,300) : ",min(10,50,-50,300))
print("min(900,530,-50,-837) : ",min(900,530,-50,837))
print("min(105,400,-500,370) : ",min(105,400,-500,370))

#pow() function
print("math.pow(10,2) : ",math.pow(10,2) )
print("math.pow(10,-2) : ",math.pow(10,-2))
print("math.pow(50,5) : ",math.pow(50,5))
print("math.pow(7,0) : ",math.pow(7,0))

#sqrt() function
print("math.sqrt(100) : ",math.sqrt(100))
print("math.sqrt(15) : ",math.sqrt(15))


print("sin(5) : ",math.sin(5))
print("sin(-5) : ",math.sin(-5))
print("sin(0) : ",math.sin(0))
print("sin(math.pi) : ",math.sin(math.pi))
print("sin(math.pi/2) : ",math.sin(math.pi/2))

print("cos(5) : ",math.cos(5))
print("cos(-5) : ",math.cos(-5))
print("cos(0) : ",math.cos(0))
print("cos(math.pi) : ",math.cos(math.pi))
print("cos(math.pi/2) : ",math.cos(math.pi/2))


print("tan(5) : ",math.tan(5))
print("tan(-5) : ",math.tan(-5))
print("tan(0) : ",math.tan(0))
print("tan(math.pi) : ",math.tan(math.pi))
print("tan(math.pi/4) : ",math.tan(math.pi/4))